import React, { Component } from 'react';
import { getUser } from '../services/userAPI';

class Header extends Component {
  constructor() {
    super();
    this.state = {
      fetchArrived: false,
    };
  }

    waitingFetch = async () => {
      const user = await getUser();
      const { name } = user;
      return name;
    }

    loggedUser = () => {
      const { fetchArrived } = this.state;
      const name = this.waitingFetch();
      if (fetchArrived === false) {
        this.setState({ fetchArrived: true });
      } else {
        return <h2>{name}</h2>;
      }
    }

    render() {
      return (
        <header data-testid="header-component">
          {this.loggedUser()}
        </header>
      );
    }
}

export default Header;
