import React, { Component } from 'react';
import Header from '../components/Header';
import searchAlbumsAPI from '../services/searchAlbumsAPI';

class Search extends Component {
  state = {
    buttonDisabled: true,
    inputContent: '',
    showTags: true,
    apiArray: [],
    loading: '',
  };

  handleChange = ({ target }) => {
    if (target.value.length < 2) {
      this.setState({
        buttonDisabled: true,
        inputContent: target.value,
      });
    } else {
      this.setState({
        buttonDisabled: false,
        inputContent: target.value,
      });
    }
  };

  handleClick = async () => {
    const { inputContent } = this.state;
    this.setState({
      buttonDisabled: true,
      inputContent: '',
      showTags: false,
      loading: 'Carregando...',
    });
    const result = await searchAlbumsAPI(inputContent);
    console.log(result);
    this.setState({
      showTags: '',
      apiArray: result,
    });
  };

  showOrNot = () => {
    const { buttonDisabled, inputContent, showTags } = this.state;
    const { loading, apiArray } = this.state;
    if (showTags) {
      return (
        <>
          <label htmlFor="artist-input">
            <input
              onChange={ this.handleChange }
              data-testid="search-artist-input"
              id="artist-input"
              placeholder="Nome do artista"
              type="text"
              value={ inputContent }
            />
          </label>
          <button
            disabled={ buttonDisabled }
            data-testid="search-artist-button"
            type="button"
            onClick={ this.handleClick }
          >
            Procurar
          </button>
        </>
      );
    }
    if (showTags === false) {
      return <h2>{loading}</h2>;
    }
    if (showTags === '') {
      return (
        <>
          <h3>
            Resultado de Ã¡lbuns de:
            {apiArray[0].artistName}
          </h3>
          {apiArray.map((item) => (
            <div key={ item.collectionId }>
              <img src={ item.artworkUrl100 } alt={ item.artistName } />
              <h5>{item.collectionName}</h5>
              <p>{item.artistName}</p>
            </div>
          ))}
        </>
      );
    }
  };

  render() {
    return (
      <div data-testid="page-search">
        <Header />
        {this.showOrNot()}
      </div>
    );
  }
}

export default Search;
