import React, { Component } from 'react';
import { Link } from 'react-router-dom/cjs/react-router-dom.min';
import Header from '../components/Header';
import { getUser, updateUser } from '../services/userAPI';

const carregando = 'Carregando...';

class ProfileEdit extends Component {
  state = {
    loading: '',
    buttonDisabled: true,
    inputName: '',
    inputEmail: '',
    inputDescription: '',
    inputImage: '',
  };

  async componentDidMount() {
    this.setState({ loading: carregando });
    const result = await getUser();
    this.setState(
      {
        loading: 'off',
        inputName: result.name,
        inputEmail: result.email,
        inputDescription: result.description,
        inputImage: result.image,
      },
    );
  }

  updatingUser = async () => {
    const { inputName, inputEmail, inputDescription, inputImage } = this.state;
    if (
      inputName.length > 0
      && inputEmail.length > 0
      && inputDescription.length > 0 && inputImage.length > 0
    ) {
      this.setState({ buttonDisabled: false });
    }
    await updateUser(
      { name: inputName,
        email: inputEmail,
        image: inputImage,
        description: inputDescription,
      },
    );
  }

  onChangeHandler = async ({ target }) => {
    const { name, value } = target;
    this.setState({ [name]: value }, this.updatingUser);
  }

  showProfile() {
    const { loading } = this.state;
    if (loading === 'off') {
      const { buttonDisabled } = this.state;
      const { inputName, inputEmail, inputDescription, inputImage } = this.state;
      return (
        <div data-testid="page-profile">
          <form>
            <input
              type="text"
              name="inputName"
              data-testid="edit-input-name"
              placeholder="Insira seu nome"
              onChange={ this.onChangeHandler }
              value={ inputName }
            />
            <input
              type="email"
              name="inputEmail"
              data-testid="edit-input-email"
              placeholder="Insira seu e-mail"
              onChange={ this.onChangeHandler }
              value={ inputEmail }
            />
            <input
              type="text"
              name="inputDescription"
              data-testid="edit-input-description"
              placeholder="Insira uma descrição"
              onChange={ this.onChangeHandler }
              value={ inputDescription }
            />
            <input
              type="text"
              name="inputImage"
              data-testid="edit-input-image"
              placeholder="Insira um link"
              onChange={ this.onChangeHandler }
              value={ inputImage }
            />
            <Link to="/profile">
              <button
                type="button"
                data-testid="edit-button-save"
                disabled={ buttonDisabled }
                onClick={ this.onClickHanlder }
              >
                Editar perfil
              </button>
            </Link>
          </form>
        </div>

      );
    }
    if (loading === '' || loading === carregando) {
      return <h2>Carregando...</h2>;
    }
  }

  render() {
    return (
      <>
        <Header />
        {this.showProfile()}
      </>
    );
  }
}

export default ProfileEdit;
