import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import Header from '../components/Header';
import InputSearch from '../components/InputSearch';
import searchAlbumsAPI from '../services/searchAlbumsAPI';

class Search extends Component {
  state = {
    buttonDisabled: true,
    inputContent: '',
    showTags: true,
    apiArray: [],
    loading: '',
    auxState: false,
  };

  componentDidUpdate() {
    const { auxState } = this.state;
    console.log(auxState);
    if (auxState) {
      this.setState({ inputContent: '', auxState: false });
    }
  }

  handleClick = async () => {
    const { inputContent } = this.state;
    this.setState({
      buttonDisabled: true,
      showTags: false,
      loading: 'Carregando...',
    });
    const result = await searchAlbumsAPI(inputContent);
    this.setState({
      showTags: '',
      apiArray: result,
    });
  };

  showOrNot = () => {
    const { buttonDisabled, inputContent, showTags } = this.state;
    const { loading, apiArray } = this.state;
    if (showTags) {
      return (
        <>
          <InputSearch
            onChange={ this.handleChange }
            data="search-artist-input"
            id="artist-input"
            placeholder="Nome do artista"
            type="text"
            value={ inputContent }
          />
          <button
            disabled={ buttonDisabled }
            data-testid="search-artist-button"
            type="button"
            onClick={ this.handleClick }
          >
            Procurar
          </button>
        </>
      );
    }
    if (showTags === false) {
      return <h2>{loading}</h2>;
    }
    if (showTags === '') {
      if (apiArray.length === 0) {
        this.setState({ auxState: true });
        return <h3>Nenhum álbum foi encontrado</h3>;
      }
      return (
        <>
          <h3>
            {`Resultado de álbuns de:  
            ${inputContent}`}
          </h3>
          {apiArray.map((item) => (
            <div key={ item.collectionId }>
              <img src={ item.artworkUrl100 } alt={ item.artistName } />
              <p>{item.artistName}</p>
              <Link
                to={ `/album/${item.collectionId}` }
                data-testid={ `link-to-album-${item.collectionId}` }
              >
                {item.collectionName}
              </Link>
            </div>
          ))}
          {this.setState({ auxState: true })}
        </>
      );
    }
  };

  handleChange = ({ target }) => {
    if (target.value.length < 2) {
      this.setState({
        buttonDisabled: true,
        inputContent: target.value,
      });
    } else {
      this.setState({
        buttonDisabled: false,
        inputContent: target.value,
      });
    }
  };

  render() {
    return (
      <div data-testid="page-search">
        <Header />
        {this.showOrNot()}
      </div>
    );
  }
}

export default Search;
