import React, { Component } from 'react';
import PropTypes from 'prop-types';
import Header from '../components/Header';
import MusicCard from '../components/MusicCard';
import getMusics from '../services/musicsAPI';
import { addSong, getFavoriteSongs } from '../services/favoriteSongsAPI';

class Album extends Component {
  constructor() {
    super();
    this.artist = '';
    this.album = '';
    this.state = {
      ApiResult: [],
      showContent: false,
      checked: false,
    };
  }

  async componentDidMount() {
    const { location } = this.props;
    const { pathname } = location;
    const id = pathname.replace(/[^0-9]/g, '');
    const result = await getMusics(id);
    console.log(result);
    this.setState({ ApiResult: result });
  }

  changeCheckedState = async (trackName) => {
    const { checked } = this.state;
    this.setState((prevState) => ({
      checked: !prevState.checked,
    }));
    if (checked === true) {
      this.setState({ loading: 'Carregando...' });
      await addSong(trackName);
      this.setState({ loading: '' });
    }
  }

  showArtist = () => {
    const { ApiResult } = this.state;
    if (ApiResult.length > 0) {
      this.artist = ApiResult[0].artistName;
      this.album = ApiResult[0].albumName;
    }
    return (
      this.artist
    );
  }

  showAlbum = () => {
    const { ApiResult } = this.state;
    if (ApiResult.length > 0) {
      this.album = ApiResult[0].collectionName;
    }
    return (
      this.album
    );
  }

  changeShowContent = () => {
    this.setState({ showContent: true });
  }

  showMusics = () => {
    const { ApiResult, showContent } = this.state;
    if (showContent) {
      return (
        <div data-testid="page-album">
          <Header />
          <h2 data-testid="artist-name">{this.showArtist()}</h2>
          <h3 data-testid="album-name">{this.showAlbum()}</h3>
          {ApiResult.lenght !== 0
            ? ApiResult.map((item, index) => (
              <MusicCard
                showContent={ this.changeShowContent }
                ApiObject={ item }
                trackId={ item.trackId }
                key={ index }
                trackName={ item.trackName }
                previewUrl={ item.previewUrl }
                checkedState={ this.changeCheckedState }
              />)) : null }
        </div>
      );
    }
    return (
      <>
        <h2>Carregando...</h2>
        <MusicCard
          showContent={ this.changeShowContent }
          ApiObject=""
          trackId=""
          key=""
          trackName=""
          previewUrl=""
          checkedState={ this.changeCheckedState }
        />
      </>

    );
  }

  render() {
    return this.showMusics();
  }
}

Album.propTypes = {
  location: PropTypes.string.isRequired,
  pathname: PropTypes.string.isRequired,
};

export default Album;
