import React, { Component } from 'react';
import { Link } from 'react-router-dom/cjs/react-router-dom.min';
import Header from '../components/Header';
import { getUser } from '../services/userAPI';

const carregando = 'Carregando...';

class Profile extends Component {
  state = {
    ApiResult: '',
    loading: '',
  };

  async componentDidMount() {
    this.setState({ loading: carregando });
    const result = await getUser();
    this.setState({ ApiResult: result, loading: 'off' });
  }

  showProfile() {
    const { loading, ApiResult } = this.state;
    if (loading === 'off') {
      return (
        <div data-testid="page-profile">
          <section>
            <img data-testid="profile-image" src={ ApiResult.image } alt="" />
            <h3>Nome:</h3>
            <p>{ ApiResult.name }</p>
            <h3>E-mail:</h3>
            <p>{ ApiResult.email }</p>
            <h3>Descrição:</h3>
            <p>{ ApiResult.description }</p>
          </section>
          <Link to={"/profile/edit"}><button type="button">Editar perfil</button></Link>
        </div>

      );
    }
    if (loading === '' || loading === carregando) {
      return <h2>Carregando...</h2>;
    }
  }

  render() {
    console.log(this.state)
    return (
      <>
        <Header />
        {this.showProfile()}
      </>
    );
  }
}

export default Profile;
