import React, { Component } from 'react';
import { Link } from 'react-router-dom/cjs/react-router-dom.min';
import Header from '../components/Header';
import { getUser, updateUser } from '../services/userAPI';

const carregando = 'Carregando...';

class ProfileEdit extends Component {
  state = {
    loading: '',
    buttonDisabled: true,
    inputName: '',
    inputEmail: '',
    inputDescription: '',
    inputImage: '',
  };

  async componentDidMount() {
    this.setState({ loading: carregando });
    await getUser();
    this.setState({ loading: 'off' });
  }

  onChangeHandler = ({ target }) => {
    const { inputName, inputEmail, inputDescription, inputImage } = this.state;
    const { name, value } = target;
    this.setState({ [name]: value });
    if (
      inputName.length > 0
      && inputEmail.length > 0
      && inputDescription.length > 0 && inputImage.length > 0
    ) {
      this.setState({ buttonDisabled: false });
    }
  }

  onClickHandler = async () => {
    const { inputName, inputEmail, inputDescription, inputImage } = this.state;
    updateUser(
      { name: inputName,
        email: inputEmail,
        image: inputImage,
        description: inputDescription,
      },
    );
  }

  showProfile() {
    const { loading, buttonDisabled } = this.state;
    if (loading === 'off') {
      return (
        <div data-testid="page-profile">
          <form>
            <input
              type="text"
              name="name"
              data-testid="edit-input-name"
              placeholder="Insira seu nome"
              onChange={ this.onChangeHandler }
            />
            <input
              type="email"
              name="email"
              data-testid="edit-input-email"
              placeholder="Insira seu e-mail"
              onChange={ this.onChangeHandler }
            />
            <input
              type="text"
              name="description"
              data-testid="edit-input-description"
              placeholder="Insira uma descrição"
              onChange={ this.onChangeHandler }
            />
            <input
              type="text"
              name="input-image"
              data-testid="edit-input-image"
              placeholder="Insira um link"
              onChange={ this.onChangeHandler }
            />
            <button
              type="submit"
              data-testid="edit-button-save"
              disabled={ buttonDisabled }
              onClick={ this.onClickHanlder }
            >
              <Link to="/profile">Salvar</Link>
            </button>
          </form>
        </div>

      );
    }
    if (loading === '' || loading === carregando) {
      return <h2>Carregando...</h2>;
    }
  }

  render() {
    return (
      <>
        <Header />
        {this.showProfile()}
      </>
    );
  }
}

export default ProfileEdit;
