import React, { Component } from 'react';
import { getUser } from '../services/userAPI';

class Header extends Component {
    waitingFetch = async () => {
      const user = await getUser();
      const { name } = user;
      return name;
    }

    loggedUser = () => {
      return <h2>{name}</h2>;
    }

    render() {
      return (
        <header data-testid="header-component">
          {this.loggedUser()}
        </header>
      );
    }
}

export default Header;
