import React, { Component } from 'react';
import { getUser } from '../services/userAPI';

class Header extends Component {
  constructor() {
    super();
    this.state = {
      fetchArrived: false,
    };
  }

  componentDidMount() {
    this.setState({ fetchArrived: true });
  }

    loggedUser = async () => {
      const { fetchArrived } = this.state;
      if (fetchArrived === false) {
        const result = <h2>Carregando...</h2>;
        return result;
      }
      if (fetchArrived === true) {
        const user = await getUser();
        const { name } = await user;
        return name;
      }
    }

    render() {
      return (
        <header data-testid="header-component">
          {console.log(this.loggedUser())}
        </header>
      );
    }
}

export default Header;
