import React, { Component } from 'react';
import PropTypes from 'prop-types';
import Header from '../components/Header';
import MusicCard from '../components/MusicCard';
import getMusics from '../services/musicsAPI';
import { addSong, getFavoriteSongs, removeSong } from '../services/favoriteSongsAPI';

const carregando = 'Carregando...';

class Album extends Component {
  constructor() {
    super();
    this.inputTarget = [];
    this.favoritesArray = [];
    this.artist = '';
    this.album = '';
    this.state = {
      ApiResult: [],
      showContent: false,
      loading: '',
    };
  }

  async componentDidMount() {
    const { location } = this.props;
    const { pathname } = location;
    const id = pathname.replace(/[^0-9]/g, '');
    const result = await getMusics(id);
    this.favoritesArray = await getFavoriteSongs();
    this.setState({ ApiResult: result });
  }

  changeLoadingState = async (trackName, target) => {
    this.inputTarget.push(target.id);
    if (target.checked !== false) {
      this.setState({ loading: carregando });
      await addSong(trackName);
      this.setState({ loading: '' });
    }
    if (target.checked === false) {
      await removeSong(trackName);
    }
  }

  showArtist = () => {
    const { ApiResult } = this.state;
    if (ApiResult.length > 0) {
      this.artist = ApiResult[0].artistName;
      this.album = ApiResult[0].albumName;
    }
    return (
      this.artist
    );
  }

  showAlbum = () => {
    const { ApiResult } = this.state;
    if (ApiResult.length > 0) {
      this.album = ApiResult[0].collectionName;
    }
    return (
      this.album
    );
  }

  changeShowContent = () => {
    this.setState({ showContent: true });
  }

  verifyngFavorites = (trackName) => {
    if (this.favoritesArray.includes(trackName)) {
      return true;
    }
    return false;
  }

  inputTargetChecked = (trackName) => {
    const result = this.inputTarget.some((item) => item === trackName);
    return result;
  }

  showMusics = () => {
    const { ApiResult, showContent, loading } = this.state;
    if (loading === '') {
      if (showContent) {
        return (
          <div data-testid="page-album">
            <Header />
            <h2 data-testid="artist-name">{this.showArtist()}</h2>
            <h3 data-testid="album-name">{this.showAlbum()}</h3>
            {ApiResult.lenght !== 0
              ? ApiResult.map((item, index) => (
                <MusicCard
                  showContent={ this.changeShowContent }
                  ApiObject={ item }
                  trackId={ item.trackId }
                  key={ index }
                  trackName={ item.trackName }
                  previewUrl={ item.previewUrl }
                  loadingState={ this.changeLoadingState }
                  checked={
                    (this.verifyngFavorites(item.trackName)
                    || this.inputTargetChecked(item.trackName))
                  }
                />)) : null }
          </div>
        );
      }
      return (
        <>
          <h2>{ carregando }</h2>
          <MusicCard
            showContent={ this.changeShowContent }
            ApiObject=""
            trackId=""
            key=""
            trackName=""
            previewUrl=""
            checkedState={ this.changeLoadingState }
            checked={ false }
          />
        </>

      );
    } if (loading === carregando) {
      return <h2>{ carregando }</h2>;
    }
  }

  render() {
    return this.showMusics();
  }
}

Album.propTypes = {
  location: PropTypes.string.isRequired,
  pathname: PropTypes.string.isRequired,
};

export default Album;
