  }

  render() {
    const { showButton } = this.state;

    return (
      <div data-testid="page-login">
        <input
          onChange={ this.handleChange }
          data-testid="login-name-input"
          type="text"
        />
        <button
          type="button"
          data-testid="login-submit-button"
          disabled={ !showButton }
