  constructor() {
    super();
    this.state = {
      showButton: false,
    };
  }

  componentDidMount() {
    console.log('montou');
    this.setState({ showButton: this.handleChange });
  }

  componentDidUpdate() {
    console.log('atualizou');
  }

  handleChange = ({ target }) => {
    const number = 3;
    const loginLength = target.value.length;
    console.log('loginLength', loginLength);
    const result = loginLength > number;
    console.log(result);
    return result;
  }

  render() {
    /* const { showButton } = this.state; */

    return (
      <div data-testid="page-login">
        <input
          onChange={ this.handleChange }
          data-testid="login-name-input"
          type="text"
        />
        <button
          type="button"
          data-testid="login-submit-button"
        >
          Entrar
        </button>
      </div>

    );
  }
}

export default Login;
