import React, { Component } from 'react';
import PropTypes from 'prop-types';

class MusicCard extends Component {
  state = {
    checkedMusicCard: '',
  }

  async componentDidMount() {
    const { showContent } = this.props;
    showContent();
  }

  callLoading = (trackName, target) => {
    const { checkedMusicCard } = this.state;
    const { loadingState } = this.props;
    loadingState(trackName, checkedMusicCard, target);
  }

  isClicked = ({ target }) => {
    const { trackName } = this.props;
    this.setState((prevState) => (
      {
        checkedMusicCard: !prevState.checkedMusicCard,
      }), () => this.callLoading(trackName, target));
  }

  render() {
    const { trackName, previewUrl, trackId, checked } = this.props;
    return (
      <div>
        <h3>
          {trackName !== undefined ? trackName : null}
        </h3>
        {trackName !== undefined || trackName === ''
          ? (
            <audio data-testid="audio-component" src={ previewUrl } controls>
              <track kind="captions" />
