import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { getFavoriteSongs } from '../services/favoriteSongsAPI';

class MusicCard extends Component {
  state = {
    loading: '',
    checked: false,
  }

  async componentDidMount() {
    const { trackName } = this.props;
    const favoritesArray = await getFavoriteSongs();
    if (favoritesArray.includes(trackName)) {
      this.setState({ checked: true });
    }
    const { showContent } = this.props;
    showContent();
  }

  isClicked = async () => {
    const { trackName } = this.props;
    const { checked } = this.state;
    this.setState((prevState) => ({ checked: !prevState.checked }));
    const { loadingState } = this.props;
    loadingState(trackName, checked);
  }

  render() {
    const { trackName, previewUrl, trackId } = this.props;
    const { loading, checked } = this.state;
    return (
      <div>
        <h3>
          {trackName !== undefined ? trackName : null}
        </h3>
        {trackName !== undefined || trackName === ''
          ? (
            <audio data-testid="audio-component" src={ previewUrl } controls>
              <track kind="captions" />
              O seu navegador n√£o suporta o elemento
              <code>audio</code>
              .
            </audio>) : null}
        {trackName !== (undefined)
          ? (
            <label htmlFor={ trackName }>
              Favorita
              <input
                data-testid={ `checkbox-music-${trackId}` }
                id={ trackName }
                type="checkbox"
                onClick={ this.isClicked }
                checked={ checked }
              />
            </label>) : null}
        <p>{loading}</p>
      </div>
    );
  }
}

MusicCard.propTypes = {
  trackName: PropTypes.string.isRequired,
  previewUrl: PropTypes.string.isRequired,
  trackId: PropTypes.string.isRequired,
  showContent: PropTypes.func.isRequired,
  loadingState: PropTypes.func.isRequired,

};

export default MusicCard;
