import React, { Component } from 'react';
import { Link } from 'react-router-dom/cjs/react-router-dom.min';
import PropTypes from 'prop-types';
import Header from '../components/Header';
import { getUser } from '../services/userAPI';
import '../Style/Profile.css';

const carregando = 'Carregando...';

class Profile extends Component {
  constructor(props) {
    super();
    this.loadingFromEdit = () => {
      if (props.location.state) {
        return props.location.state.loadingFromEdit;
      }
      return null;
    };
    this.state = {
      ApiResult: '',
      loading: '',
    };
  }

  async componentDidMount() {
    this.setState({ loading: carregando });
    const result = await getUser();
    this.setState({ ApiResult: result, loading: 'off' });
  }

  showProfile() {
    const { loading, ApiResult } = this.state;
    console.log(this.loadingFromEdit());
    if (loading === 'off' || (this.loadingFromEdit() === 'off')) {
      return (
        <div data-testid="page-profile">
          <section id="profile-section">
            <img data-testid="profile-image" src={ ApiResult.image } alt="" />
            <h3>
              Nome:
              { ApiResult.name }
            </h3>
            <h3>E-mail:</h3>
            <p>{ ApiResult.email }</p>
            <h3>Descrição:</h3>
            <p>{ ApiResult.description }</p>
          </section>
          <Link to="/profile/edit"><button type="button">Editar perfil</button></Link>
        </div>

      );
    }
    if (loading === '' || loading === carregando) {
      return <h2>Carregando...</h2>;
    }
  }

  render() {
    return (
      <>
        <Header />
        {this.showProfile()}
      </>
    );
  }
}

Profile.propTypes = {
  location: PropTypes.string.isRequired,
};

export default Profile;
